import React from 'react';
import axios from 'axios';
import Logo from './Logo';
import NavigationBar from './NavigationBar';
import SearchBar from './SearchBar';
import VideoList from './VideoList';
import VideoDetail from './VideoDetail';
import youtube from '../APIs/youtube';
import './style.css';

const DEFAULT_TERM = 'news';

class App extends React.Component {
    state = { 
        videos: [],
        selectedVideo: null,
        apiResponse: ""
    };

    callAPI = async () => {
        const res = await axios.get('http://localhost:9000/api');
        this.setState({ apiResponse: res.data });
    }

    componentDidMount() {
        this.onTermSubmit(DEFAULT_TERM);
    }

    onInfoIconSelect = async () => {
        this.callAPI();    
    };

    onTermSubmit = async (term) => {
        const response = await youtube.get('/search', {
            params: {
                q: term
            }
        });

        this.setState({ 
            videos: response.data.items,
            selectedVideo: response.data.items[0]
        });
    };

    onVideoSelect = async (video) => {
        this.setState({ selectedVideo: video });
    };

    render() {
        return (
            <div>
                <p>{this.state.apiResponse}</p>
                <div className="row" style={{height: "65px"}}>
                    <div className="ui grid">
                        <div className="column" />
                        <div className="three wide column">
                            <Logo />
                        </div>
                        <div className="eight wide column">
                            <SearchBar onTermSubmit={this.onTermSubmit} />
                        </div>
                        <div className="three wide column" />
                        <div className="column" >
                            <NavigationBar />
                        </div>
                    </div>
                </div>
                <br style={{margin: "0px 0px"}} />
                <div className="ui fitted divider" />
                <div className="margin-horizontal">
                    <div className="ui grid">
                        <div className="row">
                            <div className="twelve wide column">
                                <VideoDetail video={this.state.selectedVideo} />
                            </div>
                            <div className="four wide column">
                                <VideoList onVideoSelect={this.onVideoSelect} videos={this.state.videos} />
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    };
}

export default App;
