import React from 'react';
import Logo from './Logo';
import Navigation from './Navigation';
import {Mug} from 'react-kawaii';

const DEFAULT_MOOD = 'happy';

class About extends React.Component {

    state = {
        mood: ''
    };

    componentDidMount() {
        this.setState({mood: DEFAULT_MOOD});
    }

    onClick = () => {
        if (this.state.mood !== 'shocked') {
            this.setState({mood: 'shocked'});
        } else {
            this.setState({mood: DEFAULT_MOOD});
        }
    }

    onMouseEnter = () => {
        this.setState({mood: 'blissful'});
    }

    onMouseLeave = () => {
        this.setState({mood: DEFAULT_MOOD});
    }

    render() {
        return (
            <div>
                <p />
                <div className="row" style={{height: "65px"}}>
                    <div className="ui grid">
                        <div className="column" />
                        <div className="three wide column">
                            <Logo />
                        </div>
                        <div className="eight wide column">
                        </div>
                        <div className="two wide column" />
                        <div className="two wide column" >
                            <Navigation />
                        </div>
                    </div>
                </div>
                <p />
                <div className="ui fitted divider" />
                <div className="ui grid">
                    <div className="row"></div>
                    <div className="row">
                        <div className="five wide column" />
                        <div className="six wide column">
                        <div onClick={this.onClick} onMouseEnter={this.onMouseEnter} onMouseLeave={this.onMouseLeave}>
                            <Mug size={350} mood={this.state.mood} color="#FDA7DC" text="Hello World!" />
                        </div>
                        <div className="five wide column" />
                    </div>

                    </div>

                </div>

            </div>
        );
    }
}

export default About;
