import React from 'react';
import Navigation from './Navigation';
import ImageGallery from 'react-image-gallery';

import './style.css';

var images = [];

for (var i = 0; i < 20; i++) {
    var pagePath = './pages/p' + i.toString() + '.jpg';
    var thumbnailPath = './thumbnails/p' + i.toString() + '.jpg';

    images.push({ original: pagePath, thumbnail: thumbnailPath });
}

class App extends React.Component {

    render() {
        return (
            <div>
                <Navigation />
                <div class="ui grid">
                    <div class="row">
                        <br />
                        <div class="ui container raised segment">
                        <a class="ui red ribbon massive label" href="https://www.amazon.com/dp/B07CJ29VPJ">101 Animals</a>
                        <ImageGallery items={images} />

                        </div>
                    </div>
                    <div class="row">
                    </div>
                    <div class="row">
                        <div class="eight wide column">
                            <img src="WorldBreakerCover.png" alt="cover"></img>

                        </div>
                        <div class="eight wide column">
                        </div>
                    </div>
                </div>
            </div>
        );
    };
}

export default App;
