import React from 'react';
import { useWindowSize } from 'react-use';

import Confetti from 'react-confetti';
import Logo from './Logo';
import Navigation from './Navigation';

const About = () => {
    const { width, height } = useWindowSize();

    function drawSnowflake(ctx) {
        const numPoints = this.numPoints || (Math.random() * 5);
        this.numPoints = numPoints;
        const innerRadius = this.radius * 0.2;
        const outerRadius = this.radius * 0.8;
        ctx.beginPath();
        ctx.moveTo(0, 0 - outerRadius);

        for(let n = 1; n < numPoints * 2; n++) {
          const radius = n % 2 === 0 ? outerRadius : innerRadius;
          const x = radius * Math.sin((n * Math.PI) / numPoints);
          const y = -1 * radius * Math.cos((n * Math.PI) / numPoints);
          ctx.lineTo(x, y);
        }
        ctx.fill();
        ctx.stroke();
        ctx.closePath();
    }


    return (
        <div>
            <p />
            <div className="row" style={{height: "65px"}}>
                <div className="ui grid">
                    <div className="column" />
                    <div className="three wide column">
                        <Logo />
                    </div>
                    <div className="eight wide column">
                    </div>
                    <div className="two wide column" />
                    <div className="two wide column" >
                        <Navigation />
                    </div>
                </div>
            </div>
            <p />
            <div className="ui fitted divider" />
            <Confetti width={width} 
                      height={height} 
                      drawShape={drawSnowflake} 
                      colors={['#AEE1FF', '#CBDDF8']} 
                      gravity={0.03} 
                      wind={0.01} 
                      />
        </div>
    );
}

export default About;
